    margin-bottom:20px;
    }
    #this-block .product-outline{
        padding:0 20px 30px 20px;
        }
    [Post Wrapper]
    #this-block .product-outline-post{
    padding:15px 20px 15px 20px !important;
    position:relative;
    z-index:2;
    border-radius:10px;
    overflow:hidden;
    margin:2px 0 2px 0;
    align-items:center;
    }
    #this-block .outline-column{
    align-items:center;
    }
    [Post Title]
    #this-block .outline-title span{
        color:var(--text-color-1);
        font-size:14px;
        }
        .dark #this-block .outline-post-wrap.active .outline-title span{
            color: #fff !important;
            }
    [Post Type]
    #this-block .completed .outline_post_type{
        display:none;
        }

    #this-block .outline_post_type{
    padding:1px 6px 1px 6px;
    border-radius:50px;
    background: rgb(0 0 0 / 15%);
    font-size:11px;
    color: rgb(0 0 0 / 70%);
    }
    .dark #this-block .outline_post_type{
    background: rgb(255 255 255 / 15%);
    color: rgb(255 255 255 / 70%)
    }
    [Progress Bar]
    #this-block .completed .outline-post-progress{
        width:100%;
        }
    #this-block .outline-post-progress{
    position: absolute;
    width:100%;
    height:100%;
    top:0;
    left:0;
    z-index:-1;
    }
    #this-block .progress-0 .outline-post-progress{
    width:0;
    }
    #this-block .progress-bar-fill{
    height:100%   !important;
    background: rgb(0 0 0 / 10%);
    }
    .dark #this-block .progress-bar-fill{
    background: rgb(255 255 255 / 5%);
    }
    #this-block .completed .progress-bar-fill{
    background:rgb(0 0 0 / 10%);
    width: 100%;
    }
    .dark #this-block .completed .progress-bar-fill{
    background: rgb(255 255 255 / 5%);
    }
    [Favorited]
    #this-block .product-outline-favorite.favorited{
        opacity:1;
        color:red;
        }
        [Post Icon]
     #this-block .outline-post-icon svg path{
    fill: var(--accent-color-1);
    }
    .dark #this-block .outline-post-icon svg path{
    fill: var(--text-color-1);
    }
    .dark #this-block .active .outline-post-icon svg path{
    fill: var(--accent-color-1);
    }

    #this-block .outline_subcategory{
        margin-top: 10px;
    }
    [More]
    #this-block .main-sidebar{
        border-radius:0  !important;
        }
    /* -- Custom-Selectors Mobile --*/
    #this-block .product-outline{
    padding:0 0 30px 0;
    }

