[Playlist Wrapper]
.player__playlist{
    background: var(--bg-color-2);
    overflow: hidden;
}
.playlist{
    height: 100%;
    }
    .player__playlist{
        height: 65vh;

    }
[Playlist Title]
.playlist__title{
    padding: 10px;
    background: var(--accent-color-1);
    margin-bottom: 10px;
}
.dark .playlist__title{
background: var(--bg-color-1);
}
.media h2{
font-size: 16px;
    color: #fff;
margin: 0;
}
.dark .media h2{
color: var(--text-color-1);

}
.media-body{
    flex: 3;
    }
    .media h3{
    display: none;
    }
    .track__title{
        color: var(--text-color-1);
        font-weight: 800;
    font-size: 14px;
    }
    [Media Type]
.media-type{
order: 3;
flex: 1;
}
[Image]
.media-image{
flex: 2;
margin-right: 10px;
}
.media-image img{
border-radius: 5px;
overflow: hidden;
}

[Media Duration]
.media-type p{
margin-bottom: 5px;
}
.media-duration{
color: #ccc;
text-align: center;
font-size: 12px;
}
[Playlist body]
.playlist__body{
height: 100%;
overflow-y: scroll;
padding-bottom: 50px;
}

[Playlist Scrollbar]
.playlist__body::-webkit-scrollbar{
width:3px;
}
.playlist__body::-webkit-scrollbar-track{
background:#ddd;
}
.playlist__body::-webkit-scrollbar-thumb{
background-color:#999;
}
.dark .playlist__body::-webkit-scrollbar-track{
background: #111;
}
.dark .playlist__body::-webkit-scrollbar-thumb{
background-color:#444;
}
[Icon]
.progress__icon{
    display: none;
    }
p.track__count.video-icon svg {
    height: 10px;
}
p.track__count.video-icon svg path{
    fill: #fff;
}

p.track__count.video-icon {
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50px;
    background: rgb(0 0 0 / 40%);
margin: 0 auto 5px;
}
p.track__count.video-icon.active{
background: #FF416D;
}
p.track__count.video-icon.completed{
background: var(--accent-color-1);
}
p.track__count.video-icon.completed i{
color: #fff;
    font-size: 10px;
}

