        color: #fff;
        font-weight: 800;
    font-size: 14px;
margin: 0;
    }
    .playlist__body{
        max-height: 65vh;
        overflow-y: scroll;
        padding: 0 0 50px;
        }
[Media Image]
.media-image{
flex: 2;
margin-right: 10px;
}
.media-image img{
overflow: hidden;
}
.media-image{
    margin: 0;
    }
[Media Type]
.media-type{
    position: absolute;
    top: 10px;
    width: calc(100% - 20px);
    display: flex;
    justify-content: space-between;
    left: 10px;
    right: 10px;
    align-items: center;
}
.media-type p{
margin-bottom: 5px;
}
[Media Wrapper]
.media.track{
    overflow: hidden;
    flex-direction: column;
    position: relative;
    padding: 0;
    margin: 10px 20px;
}
[Media Duration]
.media-duration{
color: #ccc;
text-align: center;
font-size: 12px;
    background: rgb(0 0 0 /80%);
    align-items: center;
    display: flex;
    padding: 3px 6px;
    border-radius: 5px;
}
[Icon]
.progress__icon{
display: none;
}

p.track__count.video-icon svg {
    height: 10px;
}
p.track__count.video-icon svg path{
    fill: #fff;
}

p.track__count.video-icon {
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50px;
    background: var(--accent-color-1);
margin: 0 0 5px;
}
p.track__count.video-icon.active{
background: #FF416D;
}
p.track__count.video-icon.completed{
background: #16C55C;
}
p.track__count.video-icon.completed i{
color: #fff;
    font-size: 12px;
}


[Scrollbar Style]
.playlist__body::-webkit-scrollbar{
width:3px;
}
.playlist__body::-webkit-scrollbar-track{
background:#ddd;
}
.playlist__body::-webkit-scrollbar-thumb{
background-color:#999;
}
[More]
.media{
align-items: center;
}
.media-type{
order: 3;
flex: 1;
}

