/* -- Custom Selectors --*/
[Comment Title]
.comment-form-title{
    font-size: 24px;
    color: var(--text-color-1);
    margin-bottom: 20px;
    }
    [Comment Wrapper]
    .comment .media{
    align-items: flex-start;
    }
    .comment .commented{
        padding: 20px 40px;
        border-radius: 0 20px 20px 20px;
    background: #ECEDF6;
    border: 1px solid #d5d5d5;
    }

    .new_comment .media{
        align-items: flex-start;
        }
        .new_comment .media-body{
        text-align: right;
        }
        .new_comment textarea{
        padding: 20px;
        border: 1px solid #e0e0e0;
        background: #fff;
        border-radius: 0 20px 20px 20px;
        }
        .comment{
        position: relative;
        }
    [Comment Date]
    .comment-date{
    color: #aaa;
    }
    [Avatar Border]
    .comment-img{
    border: 2px solid var(--text-color-1);
    }
    [Admin Comment]
    .is_admin_true .commented{
        background: transparent;
    border: 2px solid var(--accent-color-1);
    }
    .is_admin_true .commented a{
    color: var(--accent-color-1);
    }
    .is_admin_true .commented .comment-body{
    color: var(--text-color-1);
    }
    .is_admin_true .commented .comment-date{
    color: var(--text-color-1);
    opacity: 0.6;
    }
    .is_admin_true .commented .comment-name{
    color: var(--accent-color-1)
    }
    [Colors]
    .comment-reply .comment .comment-name, .comment-reply .comment .comment-body, .comment-body,.comment-name{
    color: #000;
    }
    .comment-reply .comment .comment-date{
    color: #8D8E93;
    }
    .comment-reply .comment .reply-to-comment, .comment-edit{
    color: var(--accent-color-1);
    }
    [Reply]
    .comment-reply .comment-avatar{
    order: 2;
    }
    .comment-reply{
    width: 85%;
    margin-left: auto;
    }
    .comment-reply .comment-img{
    margin: 0 0 0 20px;
    }
    .comment-reply .commented {
        border-radius: 20px 0 20px 20px;
        background: rgba(var(--accent-color-1-rgb),0.1);
        border: 1px solid rgba(var(--accent-color-1-rgb),0.5);
    }
    .dark .comment-reply .commented{
    background: rgb(255 255 255 / 90%);
    }
    .comment-reply .comment-date{
    color: #8D8E93;
    }
    .reply-to-comment{
    margin-right: 20px;
    color: var(--accent-color-1);
    }

    [Avatar Line]
    .comment:not(.comment-reply .comment):not(:last-child):before {
        content: '';
        position: absolute;
        height: 100%;
        width: 1px;
        left: 20px;
        top: 40px;
        z-index: 0;
        background: linear-gradient(to bottom, transparent 50%, #d5d5d5 50%);
        background-size: 16px 7px, 100% 2px;
    }
    [Button]
    .comments-btn {
        background: var(--accent-color-1);
        color: #fff;
    }
    .comments-btn:hover{
        filter: brightness(1.2);
        }
        .dark .comments-btn{
            background: rgb(255 255 255 / 10%)
            }
    /* -- Custom-Selectors Mobile --*/
    [Comment Reply]
    .comment-reply{
    width: 95%;
    margin-left: auto;
    }
    .reply-to-comment{
    margin-right: 0;
    }
    .comment-reply .comment-img{
    margin: 0 0 0 10px !important;
    }
    [Comment Wrapper]
    .comment .commented{
        padding: 10px 20px;
    }
    .comment *{
    font-size: 12px;
    }
    .comment-body{
        margin: 10px 0 !important;
        }
    [Image]
    .comment-img{
    width: 30px;
    height: 30px;
    margin-right: 10px;
    }
    [Avatar Line]
    .comment:not(.comment-reply .comment):not(:last-child):before{
    left: 15px;
    top: 30px;
    }

