settings.style:
/* -- Desktop Style --*/
/* -- Custom Selectors --*/
[Post Wrapper]
.track.media{
    padding: 10px;
    margin-bottom: 10px;
}
.media{
    align-items: center;
    }

[Playlist Background]
.player__playlist{
    background: var(--bg-color-2);
    overflow: hidden;
}
.player__playlist{
    height: 65vh;
    padding-bottom: 30px;
}
.playlist__body{
height: 100%;
overflow-y: scroll;
}
.playlist{
height: 100%;
}
[Playlist Title]
.playlist__title{
    padding: 10px;
    margin-bottom: 10px;
}
.media h2{
font-size: 16px;
    color: var(--text-color-1);
margin: 0;
font-weight: 400;
}
.media-body{
    flex: 3;
    }
    .media h3{
    display: none;
    }
    .track__title{
        color: var(--text-color-1);
    font-size: 14px;
    }
[Icon Wrapper]
.media-type{
order: 3;
flex: 1;
}
[Image]
.media-image{
flex: 2;
margin-right: 10px;
}
.media-image img{
overflow: hidden;
}
[Duration]
.media-type p{
margin-bottom: 5px;
}
.media-duration{
color: #ccc;
text-align: center;
font-size: 12px;
}

[Playlist Scrollbar]
.playlist__body::-webkit-scrollbar{
width:3px;
}
.playlist__body::-webkit-scrollbar-track{
background:#ddd;
}
.playlist__body::-webkit-scrollbar-thumb{
background-color:#999;
}
.dark .playlist__body::-webkit-scrollbar-track{
background: #111;
}
.dark .playlist__body::-webkit-scrollbar-thumb{
background-color:#444;
}
[Icon]
.progress__icon{
display: none;
}
p.track__count.video-icon svg {
    height: 10px;
}
p.track__count.video-icon svg path{
    fill: #fff;
}

p.track__count.video-icon {
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50px;
    background: rgb(0 0 0 / 40%);
margin: 0 auto 5px;
}
p.track__count.video-icon.active{
background: #FF416D;
}
p.track__count.video-icon.completed{
background: var(--accent-color-1);
}
p.track__count.video-icon.completed i{
color: #fff;
    font-size: 10px;
}

