/* -- Desktop Style --*/


/* -- Mobile Style --*/

/* -- Custom Selectors --*/
[Tab Wrapper]
#this-section .tabs-wrapper {
display: flex;
    width: 100%;
    border: 1px solid rgb(0 0 0 / 10%);
    overflow-x: scroll;
    position: relative;
}
.dark #this-section .tabs-wrapper{
    border: 1px solid rgb(255 255 255 / 10%);
} 
[Tab Item]
#this-section .post-tab {
    padding: 10px 20px;
    cursor: pointer;
    position: relative;
    transition: 0.3s ease;
    flex: 1;
    font-size: 16px;
text-align: center;
color: #777;
}
.dark #this-section .post-tab{
color: #ccc;
}
#this-section .post-tab:not(:last-child){
border-right: 1px solid rgb(0 0 0 / 10%);
}
.dark #this-section .post-tab:not(:last-child){
border-right: 1px solid rgb(255 255 255 / 10%);
}
#this-section .post-tab.active:before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    height: 3px;
    width: 100%;
    background: var(--accent-color-1);
}
#this-block .post-tab i {
    font-size: 20px;
    margin-right: 5px;
}
[Responsive]
@media(max-width: 768px){
#this-section .post-tab{
    flex: 50%;
    width: 50%;
    min-width: 200px;
}
}

