[Chat Style]
#this-block .chat-heading h3{
    font-size: 24px;
    color: var(--text-color-1);
    margin-bottom: 20px;
}
#this-block .comment-img{
border: none;
}
#this-block .form-wrapper .media-body {
    display: flex;
    gap: 5px;
    align-items: center;
    margin-top: 20px;
    justify-content: center;
}
#this-block .media-body textarea {
    flex: 1;
}

#this-block .comments-btn {
    background: var(--accent-color-1);
    color: #fff;
    padding: .375rem .75rem;
}

#this-block .comment-reply {
    max-width: 100%;
    width: 100%;
    margin-right: 0;
}

#this-block .comment-reply .comment-img {
    margin: 0;
    margin: 0 10px 0 0;
}

#this-block .comment-reply .comment-avatar {
    order: 0;
}

#this-block .comment_author_details {
    display: flex;
    width: 100%;
    text-align: left;
    justify-content: flex-end;
}

#this-block .commented {
    background: transparent;
    padding: 0;
    border: none;
}

#this-block .comment-name {
    display: block;
    color: var(--text-color-1);
}

#this-block .comment-date {
    font-size: 12px;
}

#this-block .comment-body {
padding: 10px 20px;
    background: var(--accent-color-1);
    border-radius: 10px;
    display: inline-block;
    color: #fff;
}

#this-block .comment-edit {
    color: var(--text-color-1);
}

#this-block .comment-reply-wrapper {
    align-self: center;
    text-align: right;
    margin: 0 10px;
}

#this-block .initial-message img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    margin-right: 10px;
}

#this-block .initial-message {
    display: flex;
    align-items: center;
    padding-bottom: 20px;
    margin-bottom: 20px;
    border-bottom: 1px solid rgb(0 0 0 / 10%);
}

#this-block .comment:not(.comment-reply .comment):not(:last-child):before {
    content: none;
}

#this-block .new_comment textarea {
    margin: 0;
    border-radius: 10px;
    padding: 10px 20px;
}

#this-block .comment-reply .is_admin_true {
    font-weight: 400;
}

#this-block .comment-reply .is_admin_true .comment_author_details {
    justify-content: flex-start;
}

#this-block .comment-body-wrapper {
    display: flex;
    align-items: center;
    justify-content: flex-end;
}

#this-block .comment-reply .is_admin_true .comment-body-wrapper {
    justify-content: flex-start;
}

#this-block .comment-reply .is_admin_true .comment-body-wrapper .comment-body {
background: var(--bg-color-2);
    color: var(--text-color-1);
}

#this-block .comment-reply .is_admin_true .comment-avatar {
    order: 2;
}

#this-block .comment-reply .is_admin_true .comment-reply-wrapper {
    text-align: left;
}

#this-block .comment-reply .is_admin_true .comment-author {
    order: 3;
}

