settings.section_style:
/* -- Custom Selectors --*/
[Row]#this-section .row{
gap:10px;
}
[Title]
#this-section .search_title{
display:flex;
padding:0 0 0 0;
align-items:center;
justify-content:space-between;
}
#this-section .search_title h3{
margin:0 0 0 0;
font-size:18px;
}
[Reset]
#this-section .lesson-search-reset button{
background:transparent;
border:1px solid transparent;
border-bottom:2px solid #010101;
color:#010101;
padding:0 5px 0 5px;
}
[Checkbox]
#this-section .checkbox{
position:relative;
}
#this-section .checkbox input{
cursor:pointer;
width:100%;
height:100%;
z-index:2;
appearance:none;
-webkit-appearance:none;
position:absolute;
transition:0.3s ease;
border-radius:5px;
}
#this-section .checkbox input:hover{
background:rgba(0,0,0,0.05);
}
#this-section .checkbox input ~ label{
margin:0 0 0 0;
padding:3px 6px 3px 6px;
border-radius:5px;
display:block;
margin-bottom:10px;
}
#this-section .checkbox input:checked ~ label{
font-weight:600;
}
#this-section .checkbox input ~ label:after{
content:'\f00c';
position:absolute;
right:5px;
font-family:'Font Awesome 5 Pro';
font-weight:400;
display:none;
top:0;
align-items:center;
height:100%;
}
#this-section .checkbox input:checked ~ label:after{
display:flex;
}
[Dropdown]
#this-section .lesson_custom_filter.select select{
width:100%;
padding:10px 10px 10px 10px;
border:1px solid rgba(0, 0, 0, 0);
background:rgba(0,0,0,0.05);
}
[No Lesson Found]
.no_lesson{
    width: 100%;
    flex: 1;
    text-align: center;
    margin-top: 30px;
    font-size: 32px;
}

[Responsive]
@media(max-width: 768px){
#this-section{
width: 100%;
float: none;
}
#this-section .filter_title{
margin-bottom: 0;
position: relative;
font-size: 12px;
}
#this-section .filter_title:after {
    content: '\f078';
    position: absolute;
    right: 5px;
    font-family: 'Font Awesome 5 Pro';
    font-weight: 600;
    display: flex;
    top: 0;
    align-items: center;
    height: 100%;
transition: 0.3s ease;
}
#this-section .filter_title.active_acc:after{
transform: rotate(180deg)
}
#this-section .filter_title.active_acc{
margin-bottom: 10px;
}
#this-section .filter_title ~ div {
    clip-path: inset(0 0 100% 0);
    height: 0;
transition: all 0.3s ease;
}
#this-section  .filter_title ~ .show_filter_accordion{
    clip-path: inset(0 0 0% 0);
    height: auto;
    transition: all 0.3s ease;
}
}

